import { createApp } from 'vue'
import { createStore } from 'vuex'

// 创建一个新的 store 实例
const store = createStore({
  state () {
    return{
        isCollapse: false//控制菜单的展开还是收起
    }
  },
  mutations: {
    //菜单收起的方法
    collapseMenu(state) {
        state.isCollapse = !state.isCollapse
    }
}
})

const app = createApp({ /* 根组件 */ })

// 将 store 实例作为插件安装
app.use(store)
