import { createStore } from 'vuex'



export default{
    // 创建一个新的 store 实例
 state :{
    isCollapse: false//控制菜单的展开还是收起
},
 mutations :{
    //菜单收起的方法
    collapseMenu(state) {
        state.isCollapse = !state.isCollapse
    }
}
}
